// Force refresh the session before each major operation
export const refreshSession = async (): Promise<boolean> => {
  try {
    console.log("Attempting to refresh session token...");
    
    const { data: sessionData } = await supabase.auth.getSession();
    
    if (!sessionData.session) {
      console.log("No active session to refresh");
      return false;
    }
    
    // Only try to refresh if we have a session
    const { data, error } = await supabase.auth.refreshSession();
    
    if (error) {
      console.error("Failed to refresh session:", error.message);
      return false;
    }
    
    if (data.session) {
      console.log("Session refreshed successfully");
      return true;
    }
    
    return false;
  } catch (error) {
    console.error("Error refreshing session:", error);
    return false;
  }
};

// Check if current user is an admin
export const isAdminUser = async (): Promise<boolean> => {
  try {
    // First check if we have a session
    const { data: sessionData } = await supabase.auth.getSession();
    
    if (!sessionData.session) {
      console.log("No active session found");
      return false;
    }
    
    // If token is close to expiry, refresh it
    const expiresAt = sessionData.session.expires_at * 1000; // convert to milliseconds
    const now = Date.now();
    const timeToExpiry = expiresAt - now;
    
    if (timeToExpiry < 300000) { // less than 5 minutes
      console.log("Token close to expiry, refreshing session...");
      await refreshSession();
    }
    
    console.log("Found active session, checking if user is admin");
    
    // Get current user ID
    const currentUserId = sessionData.session.user.id;
    if (!currentUserId) {
      console.log("No user ID in session");
      return false;
    }
    
    console.log("Checking if user ID is admin:", currentUserId);
    
    // Query the admins table directly
    const { data: adminData, error: adminError } = await supabase
      .from('admins')
      .select('id')
      .eq('id', currentUserId)
      .maybeSingle();
      
    if (adminError) {
      console.error("Admin query error:", adminError.message);
      return false;
    }
    
    if (adminData) {
      console.log("User confirmed as admin in database");
      // Store admin info for faster checks
      localStorage.setItem('adminToken', 'authenticated');
      localStorage.setItem('adminUser', JSON.stringify({ 
        id: currentUserId,
        role: 'admin'
      }));
      return true;
    } else {
      console.log("User is not an admin");
      // Clear any stale admin tokens
      localStorage.removeItem('adminToken');
      localStorage.removeItem('adminUser');
      return false;
    }
  } catch (error) {
    console.error("Error checking admin status:", error);
    return false;
  }
};

// Generate a unique filename for uploads
export const generateUniqueFileName = (fileName: string) => {
  const timestamp = new Date().getTime();
  const randomString = Math.random().toString(36).substring(2, 12);
  const extension = fileName.split('.').pop();
  return `${timestamp}-${randomString}.${extension}`;
};

// Upload file to storage
export const uploadImageToStorage = async (file: File, bucketName = 'article_images') => {
  if (!file) {
    throw new Error('No file provided');
  }
  
  const uniqueFileName = generateUniqueFileName(file.name);
  
  // Make sure we have a fresh session
  try {
    await refreshSession();
  } catch (error) {
    console.warn("Could not refresh session before upload:", error);
    // Continue anyway, as anonymous uploads might still work
  }
  
  // Create article_images bucket if it doesn't exist
  try {
    // Check if bucket exists first by trying to get the bucket info
    const { data: bucketExists, error: bucketError } = await supabase
      .storage
      .getBucket(bucketName);
    
    if (bucketError) {
      console.warn("Error checking bucket:", bucketError);
      // Ignore error and try to continue
    }
    
    if (!bucketExists) {
      console.log(`Bucket ${bucketName} doesn't exist, creating it...`);
      const { error: createError } = await supabase.storage.createBucket(bucketName, {
        public: true,
        fileSizeLimit: 5242880 // 5MB
      });
      
      if (createError) {
        console.warn(`Error creating bucket ${bucketName}:`, createError);
        // Continue anyway, as the bucket might already exist or be auto-created
      } else {
        console.log(`Bucket ${bucketName} created successfully.`);
      }
    }
  } catch (error) {
    console.warn("Error checking/creating bucket:", error);
    // Continue anyway, as the bucket might already exist
  }
  
  // Try upload with specific policy for public uploads
  try {
    console.log(`Uploading file to ${bucketName}/${uniqueFileName}`);
    
    // Try to make the bucket public if it's not already
    try {
      await supabase.storage.updateBucket(bucketName, { public: true });
    } catch (updateError) {
      console.warn("Could not update bucket to public:", updateError);
      // Continue anyway
    }
    
    const { data, error } = await supabase
      .storage
      .from(bucketName)
      .upload(uniqueFileName, file, {
        cacheControl: '3600',
        upsert: true,  // Changed to true to avoid conflicts
      });
    
    if (error) {
      console.error("Storage upload error:", error);
      throw error;
    }
    
    if (!data) {
      throw new Error("Upload failed with no error details");
    }
    
    // Get public URL
    const { data: { publicUrl } } = supabase
      .storage
      .from(bucketName)
      .getPublicUrl(uniqueFileName);
    
    console.log("Image uploaded successfully, public URL:", publicUrl);
    return publicUrl;
  } catch (error) {
    console.error("Error uploading image:", error);
    throw error;
  }
};

